import React, { Component } from "react";
import "./App.css";
import Card from "./components/card/card";
import { checkFlipped } from "./utils/checkFlipped";

class App extends Component {
  state = {
    cards: [
      {
        id: 0,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 1,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 2,
        checked: false,
        value: 2,
        flipped: false
      },
      {
        id: 3,
        checked: false,
        value: 2,
        flipped: false
      }
    ]
  };

  cardClick = async cardIndex => {
    const { cards } = this.state;

    // to Check State of all cards
    const FilteredCards = checkFlipped(cards, cardIndex);

    // Change The State Of cards
    await this.setState({ FilteredCards });
    setTimeout(async () => {
      cards[cardIndex]["flipped"] = false;
      await this.setState({ cards });
    }, 1000);
  };
  suffle = () => {
    const { cards } = this.state;
    cards.sort(() => 0.5 - Math.random());
    this.setState({ cards });
  };
  componentWillMount() {
    suffle;
  }
  render() {
    return (
      <div className="App">
        {/* <button onClick={this.suffle} /> */}
        {this.state.cards.map(element => (
          <Card
            key={element.id}
            checked={element.checked}
            flipped={element.flipped}
            value={element.value}
            onClick={this.cardClick}
            id={element.id}
          />
        ))}
      </div>
    );
  }
}

export default App;
