import React, { Component } from "react";
import "./App.css";
import Card from "./components/card/card";
class App extends Component {
  state = {
    cards: [
      {
        id: 0,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 1,
        checked: false,
        value: 1,
        flipped: false
      }
    ]
  };
  cardClick = () => {
    this.setState({ flipped: true }, () => {
      setTimeout(() => this.setState({ flipped: false }), 1000);
    });
  };

  render() {
    return (
      <div className="App">
        <Card flipped={this.state.flipped} />
        <Card />
      </div>
    );
  }
}

export default App;
