        flipped: false
      },
      {
        id: 5,
        checked: false,
        value: 3,
        flipped: false
      },
      {
        id: 6,
        checked: false,
        value: 4,
        flipped: false
      },
      {
        id: 7,
        checked: false,
        value: 4,
        flipped: false
      },
      {
        id: 8,
        checked: false,
        value: 5,
        flipped: false
      },
      {
        id: 9,
        checked: false,
        value: 5,
        flipped: false
      },
      {
        id: 10,
