        value: 6,
        flipped: false
      },
      {
        id: 11,
        checked: false,
        value: 6,
        flipped: false
      },
      {
        id: 12,
        checked: false,
        value: 7,
        flipped: false
      },
      {
        id: 13,
        checked: false,
        value: 7,
        flipped: false
      },
      {
        id: 14,
        checked: false,
        value: 8,
        flipped: false
      },
      {
        id: 15,
        checked: false,
        value: 8,
        flipped: false
      },
      {
        id: 16,
        checked: false,
        value: 9,
        flipped: false
      },
      {
        id: 17,
        checked: false,
        value: 9,
        flipped: false
      },
      {
        id: 18,
        checked: false,
        value: 10,
        flipped: false
      },
      {
        id: 19,
        checked: false,
        value: 10,
        flipped: false
      },
      {
        id: 20,
        checked: false,
        value: 11,
        flipped: false
      },
      {
        id: 21,
        checked: false,
        value: 11,
        flipped: false
      },
      {
        id: 22,
        checked: false,
        value: 12,
        flipped: false
      },
      {
        id: 23,
        checked: false,
        value: 12,
        flipped: false
      }
    ],
    score: 0
  };

  cardClick = async cardIndex => {
    const { cards } = this.state;

    // to Check State of all cards
    const FilteredCards = checkFlipped(cards, cardIndex);

    // Change The State Of cards
    await this.setState({ FilteredCards });
    setTimeout(async () => {
      cards[cardIndex]["flipped"] = false;
      await this.setState({ cards });
    }, 1000);
  };

  suffle = () => {
    const { cards } = this.state;
    cards.sort(() => 0.5 - Math.random());
    console.log("after sort", cards);

    this.setState({ cards });
  };
  newGame = () => {
    const { cards } = this.state;
    cards.forEach(element => {
      element.flipped = false;
      element.checked = false;
    });
    this.setState({ cards });
  };
  componentWillMount() {
    this.suffle();
  }
  render() {
    return (
      <div className="App">
        <Header />
        <div className="cardsBord">
          {this.state.cards.map((element, index) => (
            <Card
              key={element.id}
              checked={element.checked}
              flipped={element.flipped}
              value={element.value}
              onClick={this.cardClick}
              id={index}
            />
          ))}
        </div>
        <Aside />
      </div>
    );
  }
}

export default App;
