import React, { Component } from "react";
import "./App.css";
import Card from "./components/card/card";
class App extends Component {
  state = {
    cards: [
      {
        id: 0,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 1,
        checked: false,
        value: 1,
        flipped: false
      }
    ]
  };
  cardClick = async cardIndex => {
    const { cards } = this.state;
    cards[cardIndex]["flipped"] = true;
    console.log("before setState", cards);
    this.setState({ cards });
    console.log("not callback", cards[cardIndex]);
  };

  render() {
    return (
      <div className="App">
        <Card
          flipped={this.state.cards[0].flipped}
          onClick={this.cardClick}
          id={this.state.cards[0].id}
        />
        <Card
          flipped={this.state.cards[1].flipped}
          onClick={this.cardClick}
          id={this.state.cards[1].id}
        />
      </div>
    );
  }
}

export default App;
