import React, { Component } from "react";
import "./card.css";
export default class Card extends Component {
  state = {
    card: {
      flipped: false
    }
  };
  onClick = () => {
    console.log("Clicked");

    this.setState({ flipped: true }, () => {
      setTimeout(this.setState({ flipped: false }), 3000);
    });
  };

  render() {
    return (
      <div className="mainCard" onClick={this.onClick}>
        {this.state.flipped ? (
          <div className="front flipeFront">Card 1 Front</div>
        ) : (
          <div className="front">Card 1 Front</div>
        )}
        {this.state.flipped ? (
          <div className="back flipeBack">Card 1 Front</div>
        ) : (
          <div className="back">Card 1 Back</div>
        )}{" "}
      </div>
    );
  }
}
