import React, { Component } from "react";
import "./index.css";
export default class Header extends Component {
  state = {
    remaningTime: 300
  };
  componentWillMount() {
    const { remaningTime } = this.state;
    setInterval(() => {
      if (remaningTime > 0)
        this.setState({ remaningTime: this.remaningTime - 1 });
    }, 1000);
  }
  render() {
    const { score } = this.props;
    const { remaningTime } = this.state;
    return (
      <div className="header">
        <div className="container">
          <span className="gameScore">
            Game Score : <span className="scoreValue">{score}</span>{" "}
          </span>
          <span className="gameName">Match Game</span>
          <span>
            Remaning Time:
            {this.state.remaningTime}
          </span>
        </div>
      </div>
    );
  }
}
