import React, { Component } from "react";
import "./index.css";
export default class Header extends Component {
  state = {
    remaningTime: 5
  };
  componentWillMount() {}

  counter = () => {
    let { remaningTime } = this.state;
    setTimeout(() => {
      if (remaningTime > 0) {
        remaningTime -= 1;
        console.log(remaningTime);
        this.setState({ remaningTime });
        this.counter();
      } else {
        this.props.onClick();
      }
    }, 1000);
  };
  render() {
    const { score } = this.props;
    const { remaningTime } = this.state;
    return (
      <div className="header">
        <div className="container">
          <span className="gameScore">
            Game Score : <span className="scoreValue">{score} </span>
          </span>
          <span className="gameName">Match Game</span>
          <span>
            Remaning Time:
            {`  ${remaningTime} s`}
          </span>
        </div>
      </div>
    );
  }
}
