import React, { Component } from "react";
import "./App.css";
import Card from "./components/card/card";
class App extends Component {
  state = {
    cards: [
      {
        id: 0,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 1,
        checked: false,
        value: 1,
        flipped: false
      },
      {
        id: 2,
        checked: false,
        value: 2,
        flipped: false
      },
      {
        id: 3,
        checked: false,
        value: 2,
        flipped: false
      }
    ]
  };

  cardClick = async cardIndex => {
    const { cards } = this.state;
    cards[cardIndex].flipped = true;

    const array = cards.find((element, index) => {
      if (element.flipped === true) {
        element.index = index;
        return element;
      }
      return null;
    });

    if (array.length > 2) {
      cards.forEach(element => (element.flipped = false));
      cards[cardIndex]["flipped"] = true;
    }
    if (array.length === 2) {
      const [first, second] = array;
      if (first.value === second.value) {
        cards[first.index].checked = true;
        cards[second.index].checked = true;
      }
    }

    // Change The State Of Items
    await this.setState({ cards });

    setTimeout(async () => {
      cards[cardIndex]["flipped"] = false;
      await this.setState({ cards });
    }, 1000);
  };

  render() {
    return (
      <div className="App">
        {this.state.cards.map(element => (
          <Card
            key={element.id}
            checked={element.checked}
            flipped={element.flipped}
            onClick={this.cardClick}
            id={element.id}
          />
        ))}
      </div>
    );
  }
}

export default App;
