import React, { Component } from "react";
import "./aside.css";
export default class aside extends Component {
  state = {
    showSide: false
  };
  ShowSide = () => this.setState({ ShowSide: !this.state.showSide });

  render() {
    const { showSide } = this.state;
    console.log(showSide);
    return (
      <div>
        <span className="showSlid" onClick={this.ShowSide}>
          <span />
          <span />
          <span />
        </span>

        {showSide ? (
          <div className="aside showSide">Hello World</div>
        ) : (
          <div className="aside">Hello World</div>
        )}
      </div>
    );
  }
}
